int64 sign = 1, val = 0; char tmp = getchar();
while (!IsNum(tmp)) { if (tmp == '-') {sign = -sign;} tmp = getchar(); }
while (IsNum(tmp)) { val = val * 10 + tmp - 48; tmp = getchar(); }
return val * sign;
