### AC代码 

```cpp
#include <cstdio>
#include <iostream>
#define maxn 120005
#define ll long long
using namespace std;
int n,g;
int a,b,fm[maxn],fz[maxn],ans[maxn*100];

int qpow(int a,int b){//快速幂
    int res=1;
    for(;b;b>>=1){
        if(b&1)res=res*a;
        a*=a;
    }
    return res;
}

//ans[0]存大整数的位数
void mul(int x){
    int k=0;//向前一步的进位
    for(int i=1;i<=ans[0];i++){
        ans[i]*=x;
        ans[i]+=k;
        k=ans[i]/10;
        ans[i]%=10;
    }
    while(k){
        ans[++ans[0]]+=k;
        k=ans[ans[0]]/10;
        ans[ans[0]]%=10;
    }
}

int main(){
    scanf("%d",&n);
    for(int i=2;i<=n;++i){
        a=n+i;//(n+2)*(n+3)*...*(2n) 分子
        for(int j=2;j*j<=a;j++){//质因数分解
            while(a%j==0)fz[j]++,a/=j;
        } 
        if(a>1) fz[a]++;
        b=i;//1*2*3*...*n 分母
        for(int j=2;j*j<=b;j++){
            while(b%j==0)fm[j]++,b/=j;
        }
        if(b>1) fm[b]++;
    }
    ans[0]=ans[1]=1;
    for(int i=2;i<=n*2;i++){
        if(fz[i]==0) continue;
        fz[i]=fz[i]-fm[i];
        if(fz[i]==0) continue;
        int x=qpow(i,fz[i]);
        if(x!=1) mul(x);
    }
    for(int i=ans[0];i>=1;--i) printf("%d",ans[i]);//注意要倒序输出
    return 0;
}
