struct Bign
{
	int c[MK],len,sign;
	//初始化 
	Bign()
	{
		memset(c,0,sizeof(c));
		len = 1;
		sign = 0;
	}
	//清高位零
	void Zero()
	{
		while(len > 1 && c[len] == 0)
		{
			len--;
		}
		if(len == 1 && c[len] == 0)
		{
			sign = 0;
		}
	} 
	//压位读入 
	void Write(char *s)
	{
		int k = 1,l = strlen(s);
		for(register int i = l - 1;i >= 0;i--)
		{
			c[len] += (s[i] - '0') * k;
			k *= 10;
			if(k == base)
			{
				k = 1;
				len++;
			}
		}
	}
	void Read()
	{
		char s[MK] = {0};
		scanf("%s",s);
		Write(s);
	}
	//输出
	void Print()
	{
		if(sign)
		{
			printf("-");
		}
		printf("%d",c[len]);
		for(register int i = len - 1;i >= 1;i--)
		{
			printf("%04d",c[i]);
		}
		printf("\n");
	} 
	//重载 = 运算符，将低精赋值给高精 
	Bign operator = (int a)
	{
		char s[100];
		sprintf(s,"%d",a);
		Write(s);
		return *this;
	} 
	//重载 < 运算符
	bool operator < (const Bign &a)const
	{
		if(len != a.len)
		{
			return len < a.len;
		}
		for(register int i = len;i >= 1;i--)
		{
			if(c[i] != a.c[i])
			{
				return c[i] < a.c[i];
			}
		}
		return 0;
	}
	bool operator > (const Bign &a)const
	{
		return a < *this;
	}
	bool operator <= (const Bign &a)const
	{
		return !(a < *this);
	}
	bool operator >= (const Bign &a)const
	{
		return !(*this < a);
	}
	bool operator != (const Bign &a)const
	{
		return a < *this || *this < a;
	}
	bool operator == (const Bign &a)const
	{
		return !(a < *this) && !(*this < a);
	}
	bool operator == (const int &a)const
	{
		Bign b;b = a;
		return *this == b;
	}
	//重载 + 运算符
	Bign operator + (const Bign &a)
	{
		Bign r;
		r.len = max(len,a.len) + 1;
		for(register int i = 1;i <= r.len;i++)
		{
			r.c[i] += c[i] + a.c[i];
			r.c[i + 1] += r.c[i] / base;
			r.c[i] %= base; 
		}
		r.Zero();
		return r;
	} 
	Bign operator + (const int &a)
	{
		Bign b;
		b = a;
		return *this + b;
	}
	//重载 - 运算符
	Bign operator - (const Bign &a)
	{
		Bign b,c;// b - c
		b = *this;
		c = a;
		if(c > b)
		{
			Bign t;
			t = b;
			b = c;
			c = t;
			b.sign = 1;
		}
		for(register int i = 1;i <= b.len;i++)
		{
			b.c[i] -= c.c[i];
			if(b.c[i] < 0)
			{
				b.c[i] += base;
				b.c[i + 1]--;
			}
		}
		b.Zero();
