		Bign b;b = a;
		return *this - b;
	}
	//重载 * 运算符 
	Bign operator * (const Bign &a)
	{
		Bign r;
		r.len = len + a.len + 2;
		for(register int i = 1;i <= len;i++)
		{
			for(register int j = 1;j <= a.len;j++)
			{
				r.c[i + j - 1] += c[i] * a.c[j];
				r.c[i + j] += r.c[i + j - 1] / base;
				r.c[i + j - 1] %= base;
			}
		} 
		r.Zero();
		return r;
	} 
	Bign operator * (const int &a)
	{
		Bign b;b = a;
		return *this * b;
	}
	//重载 / 运算符 
	Bign operator / (const Bign &b)
	{
		Bign r,t,a;
		a = b;
		if(a == 0)
		{
			return r;
		}
		r.len =len;
		for(register int i = len;i >= 1;i--)
		{
			t = t * base + c[i];
			int div,ll = 0,rr = base;
			while(ll <= rr)
			{
				int mid = (ll + rr) / 2;
				Bign k = a * mid;
				if(k > t)
				{
					rr = mid - 1;
				}else
				{
					ll = mid + 1;	
					div = mid; 
				} 
			}
			r.c[i] = div;
			t = t - a * div;
		}
		r.Zero();
		return r; 
	}
	Bign operator / (const int &a)
	{
		Bign b;b = a;
		return *this / b;
	}
	//重载 % 运算符
	Bign operator % (const Bign &a)
	{
		return *this - *this / a * a;
	}
	Bign operator % (const int &a)
	{
		Bign b;
		b = a;
		return *this % b;
	}
};
