		if (abs(a[i] - a[i + 1]) != 1)cnt++;
	}
	return cnt;
}
void dfs(int x,int eva,int step)
{
	if (step == maxstep)//达到最大步数就返回
	{
		if (!eva)d = 1;
		return;
	}


	int temp;
	for (int i = 2; i<=n; i++)
	{
		if (i == x||abs(a[i+1]-a[i])==1)continue;//这里比其他题解多了一句abs(a[i+1]-a[i])==1，因为我们不会在两个已经完美排序的两个盘子间断开，可以把他们看作一个整体了

		temp = eva;
