#endif
#endif
	scanf("%s", s1);
	len = strlen(s1);
	n = read();
	for (int i = 1; i <= n; i++) {
		scanf("%s", s2);
        int Len = strlen(s2), root = 0;
        for (register int i = 0; i < Len; i++) {
            if (!nxt[root][check(s2[i])])
                nxt[root][check(s2[i])] = ++tot;
            root = nxt[root][check(s2[i])];
