int ark(char c)//将字符处理为各个数字
{
	if(c=='A')
	{
		return 0;
	}
	if(c=='G')
	{
		return 1;
	}
	if(c=='T')
	{
		return 2;
	}
	if(c=='C')
	{
		return 3;
	}
	if(c=='?')
	{
		return 4;
	}
	return 5;
}
void make_trie(char sa[505])//建trie树 
{
	int now=0,len=strlen(sa);
	for(int i=0;i<len;i++)
	{
		int next=ark(sa[i]);
		if(!trie[now][next])
		{
			trie[now][next]=++tot;
		}
		now=trie[now][next];
	}
	num[now]++;//因为可能有重复字符串，所以这里是++，而不是赋为1
}
void dfs(int x,int now)
{
