		for(int i=0;i<=3;i++)
		{
			if(trie[now][i])
			{
				dfs(x+1,trie[now][i]);
				dfs(x,trie[now][i]);
			}
		}
	}
	if(trie[now][ark(s[x])])//如果当前字符为字母 
	{
		dfs(x+1,trie[now][ark(s[x])]);
	}
}
int main()
{
