当然数组直接开这么大肯定是不行的，要用 bitset 优化一下空间，即 `bitset<1005> vis[N]`。

## 代码

```cpp
#include <bits/stdc++.h>
using namespace std;
int n, val[150];
const int N = 3e5 + 5;
struct Trie
{
    int t[N][4], tot;
    bool tag[N];
    bitset<1005> vis[N];
    inline void insert(string s)
    {
        int cur = 0;
        for (char c : s)
        {
            if (!t[cur][c])
                t[cur][c] = ++tot;
            cur = t[cur][c];
        }
        tag[cur] = true;
    }
    inline int dfs(string s, int x, int cur)
    {
        if (vis[cur][x])
            return 0;
        vis[cur][x] = true;
        if (x == s.size() && tag[cur])
            return 1;
        int res = 0;
        if (s[x] >= 0 && t[cur][s[x]])
            return dfs(s, x + 1, t[cur][s[x]]);
        else if (s[x] == -1)
        {
            for (int i = 0; i < 4; i++)
                if (t[cur][i])
                    res += dfs(s, x + 1, t[cur][i]);
        }
        else if (s[x] == -2)
        {
            res += dfs(s, x + 1, cur);
            for (int i = 0; i < 4; i++)
                if (t[cur][i])
                    res += dfs(s, x, t[cur][i]);
        }
        return res;
    }
} tr;
inline void toval(string &s)
{
    for (int i = 0; i < s.size(); i++)
        s[i] = val[s[i]];
}
int main()
{
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    val['A'] = 0, val['C'] = 1, val['T'] = 2, val['G'] = 3, val['?'] = -1, val['*'] = -2;
    string txt;
    cin >> txt >> n;
    for (int i = 1; i <= n; i++)
    {
        string s;
        cin >> s;
        toval(s);
        tr.insert(s);
    }
    toval(txt);
    cout << n - tr.dfs(txt, 0, 0);
    return 0;
}
