	int la,lb,i,j;
	cin>>a>>b;//输入
	la=a.size();//取a串长度
	lb=b.size();//取b串长度
	a=' '+a;
	b=' '+b;
	for(i=1;i<=la;i++)
		for(j=1;j<=lb;j++){
			if(a[i]==b[j])dp[i][j]=dp[i-1][j-1]+1;//如果匹配，哪么dp[i][j]就等于dp[i-1][j-1]+1
			else dp[i][j]=max(dp[i-1][j],dp[i][j-1]);//如果不能匹配，就在dp[i-1][j]与dp[i][j-1]中取最大值
		}
	cout<<dp[la][lb];//输出
}
