const int N=10010;
//把数组的长度用常数表示是个好习惯
//当所有数组长度相同时，既可以缩短代码长度，
//又可以降低出错率，还可以实现在修改数组长度时统一修改（这既是优点，也是缺点）
int la,lb,i,j;
int dp[2][N];
char a[N],b[N];
int main(){
    scanf("%s%s",a+1,b+1);
    la=strlen(a+1);
    lb=strlen(b+1);
    for(i=1;i<=la;i++)
    for(j=1;j<=lb;j++)
    if (a[i]==b[j]) dp[i%2][j]=dp[(i-1)%2][j-1]+1;
    else dp[i%2][j]=max(dp[(i-1)%2][j],dp[i%2][j-1]);
    printf("%d",dp[la%2][lb]);
    return 0;
}
