#include<bits/stdc++.h>//万能头 
int n,a,b,c[34],d[8];//a数组代表小明彩票号码，b数组代表中奖号码，d数组代表中N等奖个数 
using namespace std;
int main()
{
	cin>>n;
	for(int i=1;i<=7;i++)//号码有7个
	{
		cin>>b;//输入中奖彩票号码
		c[b]=1; 
