    if (c == '-')
        flag = true;
    else
        num = c - '0';
    while (isdigit(c = getchar()))
        num = num * 10 + c - '0';
    return (flag ? -1 : 1) * num;
}
int prize[7];
int num,rank;
bool check(int x){
    for (int i=1;i<=7;i++)
        if (ma[i] == x)
            return true;
    return false;
}
int main(){
    n = read();
    for (int i=1;i<=7;i++)
        ma[i] = read();
    for (int _time=1;_time<=n;_time++){
        rank = 0;
        for (int i=1;i<=7;i++){
            num = read();
            if (check(num))
                rank++;
        }
        prize[7-rank]++;
    }
    for (int i=0;i<=6;i++)
        printf("%d ",prize[i]);
    return 0;
}
