int to[N],he[N],w[N],ne[N],f[N],dis[N],k=0,pre[N];
char ac[N],acc[N];
int c6(int i,int j){
	return 6*i+j; 
}
int add(int x,int y,int z,char c){
	to[++k]=y;
	ac[k]=c;
	w[k]=z;
	ne[k]=he[x];
	he[x]=k;
}
void print(int x){
    if(pre[x])print(pre[x]);
    if(acc[x])cout<<acc[x];
}
int main(){
	int h1,v1,h2,v2,h,hm,n;
	cin>>h1>>v1>>h2>>v2>>h>>hm;
	n=hm/h;
	for(int i=0;i<n;i++)
		for(int j=1;j<=6;j++){
			int x,f=c6(i,j),t=c6(i+1,j);
			cin>>x;
			add(f,t,x,'R');
		} 
	for(int i=0;i<=n;i++)
		for(int j=1;j<=5;j++){
			int x,f=c6(i,j),t=c6(i,j+1);
			cin>>x;
			add(f,t,x,'A');
		}
	for(int i=2;i<=n;i++)
		for(int j=1;j<=5;j++){
			int x,f=c6(i,j),t=c6(i-1,j+1);
			cin>>x;
			add(f,t,x,'D');
		}
   //输入
	int s=c6(h1/h,v1); //起点
	memset(dis,0x3f,sizeof(dis));
	dis[s]=0;
	q.push(M(0,s));
	while(!q.empty()){
		int x=q.top().second;
		q.pop();
		if(f[x])continue;
		f[x]=1;
		for(int i=he[x];i;i=ne[i]){
			int t=to[i],l=w[i];
			if(dis[t]>=dis[x]+l){
				pre[t]=x;		
				dis[t]=dis[x]+l;
				acc[t]=ac[i];
				q.push(M(-dis[t],t));//懒得重载运算符了
			} 
		} 
	}
   //模板
	int e=c6(h2/h,v2);//终点
	cout<<dis[e]<<endl;
	print(e);
} 
