        if (s[l+1]=='0'){  /*特判百位为0的情况*/
            if (s[l+2]=='0'){  /*特判十位为0的情况*/
                if (s[l+3]!='0') {cout << "0"; G(l+3,r);}  /*特判个位为0的情况*/
