		{
			cout<<change(now,cnt)<<' '; //先把上一次的输出
			now=op[i];cnt=1; //重置计数器 把标识设为当前位
		}
		else cnt++; //和前面相同 计数器加1
	}
	cout<<change(now,cnt);//由于最后一次还没有处理 因此直接输出
