void Euler()
{
	phi[1]=1;book[1]=1;
	for(ll i=2;i<=n;++i)
	{
		if(!book[i])
		{
			prime[++num]=i;
			phi[i]=i-1;
		}
		for(ll j=1;j<=num && i*prime[j]<=n;++j)
		{
			book[i*prime[j]]=1;
			if(i%prime[j]==0)
			{
				phi[i*prime[j]]=phi[i]*prime[j];
				break;
			}
			phi[i*prime[j]]=phi[i]*(prime[j]-1);
		}
	}
}
