## Code:

```cpp
#include<bits/stdc++.h>
#define int long long
using namespace std;
const int N=1e7+11;
bool vis[N];
int n,cnt,ans,p[N],u[N],f[N];
int read(){
	int x=0,f=1;char ch=getchar();
	while(!isdigit(ch)){if(ch=='-')f=-f;ch=getchar();}
	while(isdigit(ch)){x=x*10+ch-48;ch=getchar();}
	return x*f;
}
void prepare(){
    u[1]=1;
    for(int i=2;i<=n;i++){
        if(!vis[i]) p[++cnt]=i,u[i]=-1,f[i]=1;
        for(int j=1;j<=cnt&&i*p[j]<=n;j++){
            vis[i*p[j]]=1;
            if(i%p[j]==0){
                f[i*p[j]]=u[i];
                break;
            }
            u[i*p[j]]=-u[i];
            f[i*p[j]]=-f[i]+u[i];
        }
    }
    for(int i=1;i<=n;i++) f[i]+=f[i-1];
}
signed main(){
    n=read();
    prepare();
    for(int i=1,j;i<=n;i=j+1){
        j=n/(n/i);
        int v=n/i;v=v*v;
        ans+=(f[j]-f[i-1])*v;
    }printf("%lld\n",ans);
    return 0;
}

```

