LL S[maxn];
void init(int n)
{
    phi[0] = phi[1] = 1;
    vis[0] = vis[1] = true;
    for (int i = 2; i <= n; i++) {
        if (!vis[i])
            primes.push_back(i), phi[i] = i - 1, r[i] = i;
        for (size_t j = 0; j < primes.size() && i * primes[j] <= n; j++) {
            vis[i * primes[j]] = true;
            r[i * primes[j]] = primes[j];
            phi[i * primes[j]] = phi[i] * (primes[j] - (primes[j] < r[i]));
            if (i % primes[j] == 0)
                break;
        }
    }
}
int main()
{
    int n;
    io.read(n);
    init(n);
    for (int i = 1; i <= n; i++)
        S[i] = S[i - 1] + phi[i];
    LL ans = 0LL;
    for (size_t i = 0; i < primes.size(); i++)
        ans += (S[n / primes[i]] << 1) - 1;
    writeln(ans);
