```cpp
#include<iostream>
#include<cstdio>
#include<cmath>
#define N 10000005
#define ll long long 
using namespace std;

bool not_prime[N];
int prime[1000005];
ll sum[N];
int mu[N];
int tot;

void Mu(int n){
    mu[1]=1;
    for(int i=2;i<=n;i++){
        if(!not_prime[i]){
            prime[++tot]=i;
            mu[i]=-1;
        }
        for(int j=1;prime[j]*i<=n;j++){
            not_prime[prime[j]*i]=1;
            if(i%prime[j]==0){
                mu[prime[j]*i]=0;
                break;
            }
            mu[prime[j]*i]=-mu[i];
        }
    }
    for(int i=1;i<=tot;++i)
        for(int j=1;j*prime[i]<=n;++j)
            sum[j*prime[i]]+=(ll)mu[j];
    for(int i=1;i<=n;++i)
        sum[i]+=(ll)sum[i-1];
}

ll ans(int n,int m){
    if(n>m)swap(n,m);
    int last,i;ll re=0;
    for(i=1;i<=n;i=last+1){
        last=min(n/(n/i),m/(m/i));
        re+=(ll)(n/i)*(m/i)*(sum[last]-sum[i-1]);
    }   
    return re;
}

int main(){
    Mu(N-5);
    int T;
    int a,b;
        scanf("%d",&a);
        ll Ans=ans(a,a);
        printf("%lld\n",Ans);
    return 0;
}
