代码：

```cpp
#include<iostream>
#include<cstdio>
#include<cstring>
#include<algorithm>
#include<cstdlib>
#include<cmath>
#include<vector>
#include<queue>
#define ll long long
using namespace std;
int mu[10000001];
int cnt;
bool vis[10000001];
int prime[5000001];
void init(int n){
    mu[1]=1;
    for(int i=2;i<=n;++i){
        if(!vis[i]){
            prime[++cnt]=i;
            mu[i]=-1;
        }
        for(int j=1;j<=cnt&&i*prime[j]<=n;++j){
            int k=i*prime[j];
            vis[k]=1;
            if(i%prime[j]){
                mu[k]=-mu[i];
            }
            else{
                mu[k]=0;
                break;
            }
        }
        mu[i]+=mu[i-1];
    }
}
int main(){
    ll n;
    scanf("%lld",&n);
    init(n);
    ll ans=0;
    for(ll k=1;k<=cnt;++k){
        ll m=n/prime[k];
        ll j;
        for(ll i=1;i<=m;i=j+1){
            ll mul=m/i;
            j=min(m,m/mul);
            ans+=mul*mul*(mu[j]-mu[i-1]);
        }
    }
    printf("%lld",ans);
    return 0;
}
