				cin>>a[i];
				ocp[a[i]]=1;
			}
			int num=-1,res=0,pre;
			for(int i=20;i>=1;i--) if(!ocp[i]){
				if(num<0){
					num++;
					pre=i;
					continue;
				}
				b[++num]=pre-i-1;
				pre=i;
			}
