    for(int i=1;i<=n;i++) s[i]=s[i-1]+a[i].t1;
    memset(f,127,sizeof f);
    f[0][0][0]=0;
    for(int i=1;i<=n;i++)
        for(int j=s[i];j>=0;j--)
            for(int k=s[i];k>=0;k--)
                f[i][j][k]=min(f[i][j][k],min(max(f[i-1][j][k-a[i].t1],k+a[i].t2),max(f[i-1][j-a[i].t1][k],j+a[i].t2)));
    for(int i=0;i<=s[n];i++) 
	    for(int j=0;j<=s[n];j++)
