            ans+=2,h++,l++;
        else if(D[i][l]==D[j][h]){
            if(D[i][r]<=D[j][e]){
                if(D[i][l]==D[j][e]) ans++;
                e--,l++;
            }
            else e--,r--,ans+=2;
        }
        else l++,e--;
    }
