#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
const int N=150+5;
const int mod=12345678;
int n,m,k;
int d[N][N];
int main(){
	int i,j;
	cin>>n>>m>>k;
	int ans=0;
	for(int p=0;p<=k;p++){
		memset(d,0,sizeof(d));
		d[0][0]=1;
		for(i=0;i<=n;i++){
			for(j=0;j<=m;j++){
				if(j<i-p||j>i+k-p) continue;
				d[i+1][j]=(d[i+1][j]+d[i][j])%mod;
				d[i][j+1]=(d[i][j+1]+d[i][j])%mod;
			}
		}
		if(m<n-p||m>n+k-p) continue;
		ans=(ans+d[n][m])%mod;
	}
	k--;
	for(int p=0;p<=k;p++){
		memset(d,0,sizeof(d));
		d[0][0]=1;
		for(i=0;i<=n;i++){
			for(j=0;j<=m;j++){
				if(j<i-p||j>i+k-p) continue;
				d[i+1][j]=(d[i+1][j]+d[i][j])%mod;
				d[i][j+1]=(d[i][j+1]+d[i][j])%mod;
			}
		}
		if(m<n-p||m>n+k-p) continue;
		ans=(ans-d[n][m]+mod)%mod;
	}
	cout<<ans;
	return 0;
}

```

