思路参见思维题——51Nod 1009-数字 1 的数量

利用算1的方式算2~9的个数，再用总位数减掉1~9即为0的数量

```cpp
#include <bits/stdc++.h>
using namespace std;
#define ll long long
ll sum[10];
ll sum1[10];
ll len,len1;
ll num[20],num1[20];
ll n,m,m1,n1,h,ans,ans1;
ll f[15],ff[15];
ll fff[15];
int weishu(ll x)//算该数位数有多少
{
    int len=0;
    while(x)
    {
        num[++len]=x%10;
        x=x/10;
    }
    return len-1;
}
void init()//预处理总数
{
    f[1]=9;
    for(int i=2;i<=13;i++)
    {
        f[i]=f[i-1]*10;//i位数的个数
    }
    for(int i=1;i<=13;i++)
    {
        ff[i]=i*f[i];//i位数的总位数
    }
    for(int i=1;i<=13;i++)
    {
        fff[i]=fff[i-1]+ff[i];//求前缀和
    }
}
void solve(ll g,ll t[])//参见思维题
{
    for(int i=1; i<=9;i++)
    {
        n=g;
        h=1;
        while(n)
        {
            int ys=n%10;
            if(ys>i)
                t[i]+=(g/(h*10)+1)*h;
            else if(ys==i)
            {
                t[i]+=(g/(h*10))*h;
                t[i]+=(g%h)+1;
            }
            else
                t[i]+=(g/(h*10))*h;
            h*=10;
            n/=10;
        }
    }
}
int main()
{
    init();
    scanf("%lld%lld",&m,&m1);
    m--;
    int flag=1;
    if(m==0) flag=0;//包括m侧

    int len=weishu(m);
    int len1=weishu(m1);

    ans=fff[len]+(m-pow(10,len)+1)*(len+1);//算总位数
    ans1=fff[len1]+(m1-pow(10,len1)+1)*(len1+1);

    solve(m,sum);
    solve(m1,sum1);

    for(int i=1;i<=9;i++)//算0的数量
    {
        ans-=sum[i];
    }
    for(int i=1;i<=9;i++)
    {
        ans1-=sum1[i];
    }
    if(flag==0)
    {
        memset(sum,0,sizeof(sum));
        ans=0;
    }
    printf("%lld ",ans1-ans);
    for(int i=1;i<=9;i++)
    {
        i==9 ? printf("%lld\n",sum1[i]-sum[i]):printf("%lld ",sum1[i]-sum[i]);
    }
    return 0;
}

