```cpp
/*Tokgo*/
#include <bits/stdc++.h>
#define rep(i,a,b) for(int i=a;i<=b;++i)
#define dwn(i,a,b) for(int i=a;i>=b;--i)

using namespace std;

typedef long long ll ;  

ll a , b ; 

ll cnt[10] ;
ll f[ 15 ][ 10 ][ 10 ]; // i位填j时，k的数量 

int l , c[ 20 ] ;
void tran( ll a ) {
	l = 0 ;
	while( a )
		c[ ++l ] = a % 10 , a /= 10 ;
} 

void solve( int op , ll a ) {
	dwn( i , l , 1 ) {
		rep( j , (i==l) , c[i]-1 ) 
			rep( k , 0 , 9 ) cnt[k] += f[i][j][k]*op ;
		a -= ( c[i] * pow( 10,i-1 ) ) ;
		cnt[c[i]] += (a+1)*op ; 
	}
	rep( i , 1 , l-1 ) 
		rep( j , 1 , 9 ) 
			rep( k , 0 , 9 ) cnt[k] += (f[i][j][k])*op ;
}

int main() {

	rep( i , 0 , 9 ) f[1][i][i] = 1 ;
	rep( i , 2 , 13 )
		rep( j , 0 , 9 ) 
			rep( k , 0 , 9 ) {
				if( k == j ) f[i][j][k] = pow( 10 , i-1 ) ;
				rep( p , 0 , 9 )
					f[i][j][k] += f[i-1][p][k] ; 
			}		

	cin >> a >> b ; 

	tran( b ) , solve( 1 , b ) ;
	tran( a ) , solve( -1 , a-1 ) ;

	rep( i , 0 , 9 ) cout << cnt[i] << " " ;

	return 0;
}


