    s-=w[tot]*pow(10,tot-1);//减去最高位就是最高位要用的次数减1
    cnt1[w[tot]]+=s+1;
    for(int i=tot-1;i>=1;--i){
        s-=w[i]*pow(10,i-1);
        cnt1[w[i]]+=s+1;
        for(int j=0;j<w[i];++j){
            for(int k=0;k<=9;++k){
