        for(long long i=1;i<=12;++i)
            for(long long j=0;j<=9;++j)
                for(long long k=0;k<=9;++k)
                    for(long long l=0;l<=9;++l)
                        if(l==k)dp[i+1][k][l]+=dp[i][j][l]+power[i-1];
                        else dp[i+1][k][l]+=dp[i][j][l];//预处理
        long long a,b;
