```cpp
#include <bits/stdc++.h>

using namespace std;

const int maxn=1000010;


long long dp[maxn][3];
struct edge{
	int next,to;
}e[maxn<<2];

int n,val[maxn],head[maxn],vis[maxn],cnt,E,x1,x2;

inline int read(){
	int f=1,x=0;
	char c=getchar();
	while(c<'0'||c>'9'){
		if(c=='-')f=-1;
		c=getchar();
	}
	while(c>='0'&&c<='9'){
		x=x*10+c-'0';
		c=getchar();
	}
	return f*x;
}

inline void add(int u,int v){
	e[++cnt].next=head[u];
	e[cnt].to=v;
	head[u]=cnt;
}

void findloop(int now,int fz){
	vis[now]=1;
	for(int i=head[now];i;i=e[i].next){
		int v=e[i].to;
		if(v==fz)continue;
		if(vis[v]){
			x1=now;
			x2=v;
			E=i;
			continue;
		}
		findloop(v,now);
	}
}

void dfs(int now,int fz){
	dp[now][0]=0;
	dp[now][1]=val[now];
	for(int i=head[now];i;i=e[i].next){
		int v=e[i].to;
		if(v==fz)continue;
		if((x1==now&&x2==v)||(x2==now&&x1==v))continue;
		dfs(v,now);
		dp[now][0]+=max(dp[v][0],dp[v][1]);
		dp[now][1]+=dp[v][0];
	}
}

int main(){
	long long ans=0;
	n=read();
	for(int i=1;i<=n;i++){
		val[i]=read();
		int qwq=read();
		add(qwq,i);
		add(i,qwq);
	}
	for(int i=1;i<=n;i++){
		if(vis[i])continue;
		findloop(i,-2);
		dfs(x1,-1);
        long long temp=dp[x1][0];
        dfs(x2,-1);
        temp=max(temp,dp[x2][0]);
        ans+=temp; 
	}
	printf("%lld",ans）;
	return 0;
}
