```cpp
#include<bits/stdc++.h>
#define ll long long
using namespace std;
int n,a[1000005];
ll d[1000005][2],g[1000005][2],ans;
int to[1000005],pa[1000005];
bool vis[1000005];
queue<int>q;
vector<int>v[1000005];
vector<int>cir;
void read(int& x)
{
	char c=getchar();
	x=0;
	while(c<'0'||c>'9')c=getchar();
	while(c>='0'&&c<='9')
	{
		x=x*10+c-'0';
		c=getchar();
	}
}
ll circle(int x)
{
	ll ans1,ans2;
	cir.clear();
	vis[x]=1;
	cir.push_back(x);
	g[x][0]=d[x][0];
	g[x][1]=d[x][1];
	for(int i=to[x];i!=x;i=to[i])
	{
		cir.push_back(i);
		vis[i]=1;
		g[i][0]=d[i][0];
		g[i][1]=d[i][1];
	}
	d[cir[0]][0]=-0x3f3f3f3f;
	d[cir[0]][1]=g[cir[0]][1];
	for(int i=1;i<cir.size();i++)
	{
		d[cir[i]][0]=g[cir[i]][0]+max(d[cir[i-1]][0],d[cir[i-1]][1]);
		d[cir[i]][1]=g[cir[i]][1]+d[cir[i-1]][0];
	}
	ans1=d[cir[cir.size()-1]][0];
	d[cir[0]][0]=g[cir[0]][0];
	d[cir[0]][1]=-0x3f3f3f3f;
	for(int i=1;i<cir.size();i++)
	{
		d[cir[i]][0]=g[cir[i]][0]+max(d[cir[i-1]][0],d[cir[i-1]][1]);
		d[cir[i]][1]=g[cir[i]][1]+d[cir[i-1]][0];
	}
	ans2=max(d[cir[cir.size()-1]][0],d[cir[cir.size()-1]][1]);
	return max(ans1,ans2);
}
int main()
{
	read(n);
	for(int i=1;i<=n;i++)
	{
		read(a[i]);
		read(to[i]);
		v[to[i]].push_back(i);
		pa[to[i]]++;
		d[i][1]=a[i];
	}
	for(int i=1;i<=n;i++)
	{
		if(!pa[i])q.push(i);
	}
	while(!q.empty())
	{
		int fa=q.front();
		q.pop();
		d[fa][1]=a[fa];
		vis[fa]=1;
		for(int i=0;i<v[fa].size();i++)
		{
			int y=v[fa][i];
			d[fa][0]+=max(d[y][0],d[y][1]);
			d[fa][1]+=d[y][0];
		}
		pa[to[fa]]--;
		if(!pa[to[fa]])q.push(to[fa]);
	}
	for(int i=1;i<=n;i++)
	{
		if(vis[i])
