				d[fa][0]+=max(d[i][0],d[i][1]);
				d[fa][1]+=d[i][0];
			}
		}
	}
	for(int i=1;i<=n;i++)
	{
		if(!vis[i])ans+=circle(i);
	}
	printf("%lld\n",ans);
}
