        	    g[root[x]][0]=max(g[root[x-1]][1],g[root[x-1]][0])+f[root[x]][0];g[root[x]][1]=g[root[x-1]][0]+f[root[x]][1];
        	}long long ans=max(g[root[m]][1],g[root[m]][0]);
