# Solution
一开始我想着贪心,但是贪心是错误的,因为可能很多人都仇恨一个人但是仇恨的这个人的权值最大.

所以我们考虑,如果两个点不在一个仇恨集合里面,显然不会互相影响.

那么就可以对于每一个集合树形DP,然后累加答案

```cpp
#include<stdio.h>
#include<stdlib.h>
#include<string.h>
#include<math.h>
#include<algorithm>
#include<iostream>
#include<queue>
#define re register
#define ll long long
using namespace std;
inline int gi(){
    int f=1,sum=0;char ch=getchar();
    while(ch>'9' || ch<'0'){if(ch=='-')f=-1;ch=getchar();}
    while(ch>='0' && ch<='9'){sum=(sum<<3)+(sum<<1)+ch-'0';ch=getchar();}
    return f*sum;
}
struct node{
    int val,heat;
    bool operator<(const node b)const{
        return val>b.val;
    }
}Knight[1000010];
int front[1000010],cnt=1,x1,x2,E;
struct edge{
    int to,nxt;
}e[2000010];
void Add(int u,int v){
    e[++cnt]=(edge){v,front[u]};front[u]=cnt;
}
ll ans,dp[1000010][2];
int vis[1000010],n;
void find(int u,int fa){
    vis[u]=1;
    for(re int i=front[u];i;i=e[i].nxt){
        int v=e[i].to;
        if(v==fa)continue;
        if(vis[v]){
            x1=u;x2=v;E=i;continue;
        }
        find(v,u);
    }
}
void dfs(int u,int fa){
//  printf("%d %d\n",u,fa);
    dp[u][0]=0;dp[u][1]=Knight[u].val;
    for(re int i=front[u];i;i=e[i].nxt){
        int v=e[i].to;
        if(v==fa)continue;
        if(i==E || (i^1)==E)continue;
        dfs(v,u);
        dp[u][0]+=max(dp[v][1],dp[v][0]);
        dp[u][1]+=dp[v][0];
    }
}
int main(){
    n=gi();
    for(re int i=1;i<=n;i++){
        Knight[i].val=gi();
        Knight[i].heat=gi();
        Add(i,Knight[i].heat);
        Add(Knight[i].heat,i);
    }
    for(re int i=1;i<=n;i++)
        if(!vis[i]){
            find(i,i);
            dfs(x1,x1);
            ll tmp=dp[x1][0];
            dfs(x2,x2);
            tmp=max(tmp,dp[x2][0]);
            ans+=tmp;
        }
    printf("%lld\n",ans);
    return 0;
}
