#include<bits/stdc++.h>
using namespace std;

const int mod = 19260817;

int A, B;

inline int read() {
	static char c;
	int x = 0;
	while(!isdigit(c = getchar()));
	x = c ^ 48;
	while(isdigit(c = getchar())) 
		x = (x * 10 + (c ^ 48)) % mod;

	return x;
}

inline int expow(int x, int k) {
	int base = 1;
	for(; k; k >>= 1) {
		if(k & 1) base = 1LL * base * x % mod;
		x = 1LL * x * x % mod;
	} 

	return base;
}

int main() {
	A = read(), B = read();
	if(B == 0) return puts("Angry!"), 0;
	printf("%lld\n", 1LL * A * expow(B, mod - 2) % mod);
	return 0;
} 
