#include<bits/stdc++.h> 
#define LL long long
#define MOD 1000000007
using namespace std;

int n,i,j,len,m,p;
double t,ans;
char c[105][105];

double mb(int x,int y)
{
    if(y==0) return 1.0;
    if(y==1) return 0.5;
    if(y==2) return 0.25;
    if(y==3) return 0.125;
}

int main()
{   
    scanf("%d%lf",&n,&t);

    while(cin>>c[i])
    {   

        len=strlen(c[i]);

        for(j=0;j<len;j++)
        {
            if((c[i][j]!='.')&&(c[i][j]!='(')&&(c[i][j]!=')')) ans+=mb(2,p);
            if(c[i][j]=='(') p++;\\这里是计算括号层数
            if(c[i][j]==')') p--;
            if(c[i][j]=='.')
            {
                if(c[i][j-1]=='-') ans+=1.0;
                else ans+=mb(2,p)*0.5;\\附点音符的处理         
            }       
        }

        i++;            
    }

    m=(ans*60.0)/t;
    printf("%d",m);
    return 0;
}
```

