```cpp
#include<bits/stdc++.h>
using namespace std;
int i,j,N;
struct huanfan
{
	int h;
	int l;
}t[1600];
int t1[40][40];
int main()
{
	cin>>N;
	for(i=1;i<=N*N;i++)
