#include<cstring>
using namespace std;
int n;
struct zb{
	int x;
	int y;
}map[1650];	
int a[45][45]; 
int main(){
	cin>>n;
	memset(map,0,sizeof(map));//初始化为零 
	memset(a,0,sizeof(a));
	map[1].x=1;//求1所在的位置，并在a数组中赋值 
	map[1].y=n/2+1;
	a[map[1].x][map[1].y]=1;
	for(int i=2;i<=n*n;i++){
		if(map[i-1].x==1 && map[i-1].y==n){//(K-1) 在第一行最后一列的情况
			map[i].x=2;map[i].y=n;
			a[map[i].x][map[i].y]=i;
			continue;
		}
		if(map[i-1].x==1){// (K-1)在第一行但不在最后一列的情况
			map[i].x=n;map[i].y=map[i-1].y+1;
			a[map[i].x][map[i].y]=i;
			continue;
		}
		if(map[i-1].y==n){// (K-1)不在第一行但在最后一列的情况
			map[i].x=map[i-1].x-1;
			map[i].y=1;a[map[i].x][map[i].y]=i;
			continue;
		}
		//剩下一种情况， (K-1) 既不在第一行，也不在最后一列 
		//先判断 (K-1)的右上有没有填数 
		if(a[map[i-1].x-1][map[i-1].y+1]==0){
			map[i].x=map[i-1].x-1;
			map[i].y=map[i-1].y+1;
			a[map[i].x][map[i].y]=i;
			continue;
		}
		//若右上方有数，则只能放在 (K-1) 正下方 
		map[i].x=map[i-1].x+1;
		map[i].y=map[i-1].y;
		a[map[i].x][map[i].y]=i;
	}
	//输出幻方 
	for(int i=1;i<=n;i++){
		for(int j=1;j<=n;j++){
			cout<<a[i][j]<<" ";
		}
		cout<<endl;
	}
	return 0;
}
