    int i=1,j=n/2+1,k=1; //i,j为当前位置,k为要填的数字 
    a[i][j]=k;
    while (k<n*n){
          k++; i--; j++;
          if (i<1 && j>n){  //出右上界 
             i+=2; j--;
             a[i][j]=k;
             continue;
          }
          if (i<1){        //出上界 
             i=n;
             a[i][j]=k;
             continue;
          }
          if (j>n){        //出右界 
             j=1;
             a[i][j]=k;
             continue;
          }
          if (a[i][j]==0) a[i][j]=k; //没数直接填 
          else{
             i+=2; j--;    //右上有数往下填 
             a[i][j]=k;
          }
    }
    for (i=1;i<=n;i++)
        for (j=1;j<=n;j++)
            printf("%d%c",a[i][j],j==n?'\n':' '); //输出幻方 
