#include <bits/stdc++.h>

using namespace std;

int n , m;
int a[100][105];
bool vis[2005];

int change (int s , int opt) {
	int now = s;
	for(int i = 1 ; i <= n ; ++ i) {
		if(a[opt][i] == 1) {
			if(now & (1 << (i - 1))) {
				now = ~now;
				now |= (1 << (i - 1));
				now = ~now;
			}
		}  
		if(a[opt][i] == -1) now |= (1 << (i - 1));  
	}
	return now;
}

void Bfs() {
	queue <pair<int , int> > q;
	int sta = 0;
	sta |= (1 << n); sta -= 1;
	vis[sta] = 1;
	q.push(make_pair(sta , 0));
