```cpp
    #include<iostream>
    #include<cmath>
    using namespace std;
    bool isPrime(int inp)        //判断质数
    {
        int a = sqrt(inp);        //储存inp开方后的结果，提高效率
        for (int i = 2; i <= a; i++)
            if (inp%i == 0)
                return false;
        return true;
    }
    void dev(int num)        //输出
    {
        cout << num << "=";
        while (!isPrime(num))
        {
            while (num % 2 == 0 && num != 2) { num /= 2; cout << 2 << "*"; }
            for (int i = 3; i < num; i+=2)
                if (num%i == 0 && isPrime(i)) { cout << i << "*"; num /= i; break; }
        }
        cout << num << endl;
    }
    int main()
    {
        int a = 1, b = 1, n;
        bool isA;
        cin >> n;            //数列中第n个数
        isA = n % 2 == 0 ? false : true;        //判断这个数对应的是变量a还是b
        n = (n + 1) / 2 - 1;                    //总共的循环次数
        for (int i = 0; i < n; i++)
        {
            a += b; b += a;
            if (b < 0)b += 2147483648;
            if (a < 0)a += 2147483648;
        }
        dev(isA ? a : b);
        return 0;
}
