```cpp
//最后分解质因数就ok了
#include<iostream>
#include<cstdio>
#include<cstring>
#define MAXN 20
#define ll long long
using namespace std;
int n=2;
const ll p=(1<<31);
void write(ll x){//输出优化
    if(x>9) write(x/10);
    putchar(x%10+48);
}
struct Mat{//定义一个结构体，使用起来方便
    ll m[MAXN][MAXN];
    Mat() {}
    Mat(int x){
        Mat_init();
        for(int i=1;i<=MAXN;i++) m[i][i]=x;
    }
    void Mat_init(){ memset(m,0,sizeof(m));}
    Mat operator*(Mat const &B)const{
        Mat C;
        C.Mat_init();
        for(int i=1;i<=n;i++){
            for(int j=1;j<=n;j++){
                for(int k=1;k<=n;k++){
                    C.m[i][j]=(C.m[i][j]+(m[i][k]*B.m[k][j]))%p;
                }
            }
        }
        return C;
    }
    Mat operator+(Mat B){
        Mat C;
        for(int i=1;i<=n;i++){
            for(int j=1;j<=n;j++){
                C.m[i][j]=(m[i][j]+B.m[i][j])%p;
            }
        }
        return C;
    }
    Mat operator^(int k){//矩阵快速幂
        Mat A= *this,ans(1);
        while(k){
            if(k&1) ans=ans*A;
            A=A*A;
            k>>=1;
        }
        return ans;
    }
}A;
ll read(){//读入优化
    ll out=0;
    char c=getchar();
    while(c<48||c>57) c=getchar();
    while(c>=48&&c<=57){
        out=(out<<1)+(out<<3)+c-48;
        c=getchar();
    }
    return out;
}
void init(){
    A.m[1][1]=1;
    A.m[1][2]=1;
    A.m[2][1]=1;
    A.m[2][2]=0;
}
void print(Mat x){
    for(int i=1;i<=n;i++){
        for(int j=1;j<=n;j++){
            write(x.m[i][j]);
            putchar(' ');
        }
        puts("");
    }
}
ll fib(int k){
    Mat ans=A^k;
    return ans.m[1][2];
}
void divide(ll x){//分解质因数
    if(x<=3){
        write(x);
        return;
    }
    else{
        ll nn=x;
        for(int i=2;i<=nn;i++){
            while(!(x%i)){//从2开始，除到没有这个因子为止
                write(i);
                x/=i;
                if(x>1) putchar('*');
            }
            if(i>x) break;//i>x时说明已经分解完毕
        }
    }
}
void solve(){
    int k;
    cin>>k;
    init();
    Mat ans=A^k;
    write(ans.m[1][2]);
    putchar('=');
    divide(ans.m[1][2]);
}
int main(){
    solve();
    return 0;
}
