    int n=0,len=0;//定义变量
    scanf("%d",&n);//读入数据
    k=n;//赋值
    for(int i=3;i<=n;i++)//开始推斐波那契数列
    {
        f[i]=f[i-1]+f[i-2];//推理公式
    }
    printf("%d=",f[n]);//输出（一部分）
    while(1)//无限循环
    {
        int t=sqrt(f[n]+1);//判断素数
        bool flag=true;
        for(int i=2;i<=t;i++)
        {
            if(pd(i)==1)//如果这个数是素数
            {
                len++;//存数下去
                ans[len]=i;
                f[n]/=i;
                flag=false;
            }
