    unsigned int a1=1,a2=1,a3;
    for(int i=3;i<=n;i++)
    {
        a3=a1+a2;
        a1=a2;
        a2=a3;
    }
    return a3;
}
bool zz(int i)//判断质数
{
    if(i==2) return 1;
    if(i%2==0) return 0;
    if(i<2) return 0;
    int t=sqrt(i);
    for(int j=3;j<=t;j+=2)
    {
        if(i%j==0) return 0;
    }
    return 1;
}
void fj(int i)
{
    if(zz(i))
    {
        cout<<i<<'='<<i;
        return;
    }
    if(i==1||i==2)//特判特殊情况
    {
        cout<<"1=1\n";
        return;
    }
    cout<<i<<'=';
    int t=2;
    while(i%t) t++;//算出最小的质因数
    cout<<t;
    i/=t;
    while(i>1)
    {
        if(i%t==0)
        {
            cout<<'\*'<<t;
            i/=t;
        }
        else t++;
    }
}
int main()
{
    cin>>n;
    n=f();
    fj(n);
    return 0;
}

