#include<bits/stdc++.h>
using namespace std;
int main(){
	string s;
	getline(cin,s);
	int n,a,len=s.length(),top=-1;
	scanf("%d",&n);
	for(int i=1;i<=n;i++){
		scanf("%d",&a);
		top+=a;
		top=min(top,len-2);
		if(s[top]=='>'&&s[top+1]=='>'&&s[top+2]=='>')
		for(int j=top;j<len;j++){
			if(s[j]!='>'){
				top=j;
				break;
			}
		}
		else if(s[top]=='*'&&s[top+1]=='*'&&s[top+2]=='*'){
			int m=0;
			for(int j=top;j<len;j++)
			if(s[j]=='*') m++;
			else break;
			top-=m;
		}
		top=min(top,len-2);
		top=max(top,0);
	}
	printf("%d %d",top+1,len-top-2);
}
```
