### code:

```cpp
#include<bits/stdc++.h>
using namespace std;
int a,som;
int s[1000001],m[1000001][21];
inline int r() {//快读 
	int s=0,w=1;
	char ch=getchar();
	while(ch<'0'||ch>'9') {
		if(ch=='-')w=-1;
		ch=getchar();
	}
	while(ch>='0'&&ch<='9') s=s*10+ch-'0',ch=getchar();
	return s*w;
}
inline int query(int n,int l){//求区间最小值 
	int p=log2(l-n+1);
	return min(m[n][p],m[l-(1<<p)+1][p]);
}
int main() {
	a=r();
	memset(m,0x7f,sizeof(m));//初始化为inf 
	for(int i=1; i<=a; i++) {
		s[i]=r();
		s[i]+=s[i-1];//前缀和维护 
		m[i][0]=s[i];
	}
	for(int i=1; (1<<i)<=a; i++)
		for(int j=1; j+(1<<i)-1<=a; j++)
			m[j][i]=min(m[j][i-1],m[j+(1<<(i-1))][i-1]);//ST表维护 
	if(query(1,a)>=0)//特判k是否合法 
	som++;
	for(int i=2; i<=a; i++) {
		if(query(i,a)-s[i-1]>=0)//判断k是否合法 
			if(query(1,i-1)+s[a]-s[i-1]>=0)//判断k是否合法 
				som++;
	}
	cout<<som;
	return 0;
}
```
