char s[N],w[]="KXVMCNOPHQRSZYIJADLEGWBUFT";
map<char,int> q;
ll read()
{
    ll res=0,chr=getchar();
    while(!isdigit(chr)&&chr!=EOF)
    {
    	chr=getchar();
	}
    while(isdigit(chr))
    {
        res=(res*10)+(chr-'0');
        chr=getchar();
    }
    return res;
}
int main()
{
	n=read();
	scanf("%s",s);
	len=strlen(s);
	for(int i=1;i<=len;i++)
	{
		str[i]=s[i-1];
	}
   //前面都是输入
	for(int i=1;i<=n;i++)
