  for (int s = n; s; s--) {
    if (op[s] == 1) {
      int l = a[s];
      for (int i = 1, j = 1, k = 1; i <= len; i++, j += l) {
        if (j > len) j = ++ k;
        tmp[j] = ch[i];
      }
      memcpy(ch, tmp, sizeof tmp);
    }
    if (op[s] == 2) {
      int key_len = str[s].length();
      for (int i = 1, j = 1; i <= len; i++, j++) {
