      for (int i = 1; i <= len; i++)
        ch[i] = qwe[ch[i] - 'a'];
    }
  }
  for (int i = 1; i <= len; i++)
    if (bg[i]) putchar(toupper(ch[i]));
    else putchar(ch[i]);
