```cpp
//c++代码，2015年初赛提高组完善程序第一题
#include<iostream>
using namespace std;
const int MAXN=1000000;
long long n,i,ans,sum;
int x[MAXN];
int lmax[MAXN];//lmax[i]为仅含x[i]及x[i]左侧整数的连续子序列的序列和，最大的序列和
int rmax[MAXN];//rmax[i]为仅含x[i]及x[i]右侧整数的连续子序列的序列和，最大的序列和
int main()
{
    cin>>n;
    for(i=0;i<n;++i)
    cin>>x[i];
    lmax[0]=x[0];
    for(i=1;i<n;++ i)
      if(lmax[i-1]<=0)
        lmax[i]=x[i];
      else lmax[i]=lmax[i-1]+x[i];
    for(i=1;i<n;++i)
      if(lmax[i]<lmax[i-1])
        lmax[i]=lmax[i-1];
    rmax[n-1]=x[n-1];
    for(i=n-2;i>=0;--i)
      if(rmax[i+1]<=0)
        rmax[i]=x[i];
      else rmax[i]=rmax[i+1]+x[i];
    for(i=n-2;i>=0;--i)
      if(rmax[i]<rmax[i+1])
        rmax[i]=rmax[i+1];
    ans=x[0]+x[2];
    for(i=1;i<n-1;i++)
    {
        sum=lmax[i-1]+rmax[i+1];
        if(sum>ans)ans=sum;
    }
    cout<<ans<<endl;
    return 0;
}
```
